import React, { useState } from 'react';
import { Button, Checkbox, Form } from 'semantic-ui-react';
import axios from 'axios';
import { useNavigate} from 'react-router-dom';


export default function Create() {
    const [id, setID] = useState(null);
    const [telefone, setTelefone] = useState('');
    const [cpf, setCPF] = useState('');
    const [nome, setNome] = useState('');
    const [email, setEmail] = useState('');
    const [senha, setSenha] = useState('');
    const [datanasc, setData] = useState('');
    const [cep, setCep] = useState('');
    const [bairro, setBairro] = useState('');
    const [cidade, setCidade] = useState('');
    const [estado, setEstado] = useState('');
    let history = useNavigate();

    const postData = () => {
        axios.post(`http://localhost:8080/alunos`, {
            telefone,
            cpf,    
            nome, 
            email, 
            senha,
            datanasc, 
            cep, 
            bairro, 
            cidade,
            estado
        }).then(() => {
            history('/read')
        })
    }
    return (
        <div>
            <Form className="create-form">
                <Form.Field>
                    <label>Nome</label>
                    <input placeholder='Nome' onChange={(e) => setNome(e.target.value)}/>
                </Form.Field>
                <Form.Field>
                    <label>E-mail</label>
                    <input placeholder='Email' value    ={email} onChange={(e) => setEmail(e.target.value)}/>
                </Form.Field>
                <Form.Field>
                    <label>CEP</label>
                    <input placeholder='CEP' value={cep} onChange={(e) => setCep(e.target.value)}/>
                </Form.Field>
                <Form.Field>
                    <label>CPF</label>
                    <input placeholder='CPF' value={cpf} onChange={(e) => setCPF(e.target.value)}/>
                </Form.Field>
                <Form.Field>
                    <label>Cidade</label>
                    <input placeholder='Cidade' value={cidade} onChange={(e) => setCidade(e.target.value)}/>
                </Form.Field>
                <Form.Field>
                    <label>Estado</label>
                    <input placeholder='Estado' value={estado} onChange={(e) => setEstado(e.target.value)}/>
                </Form.Field>
                <Form.Field>
                    <label>Telefone</label>
                    <input placeholder='Telefone' value={telefone} onChange={(e) => setTelefone(e.target.value)}/>
                </Form.Field>
                <Form.Field>
                    <label>Data de Nascimento</label>
                    <input placeholder='Data de Nascimento' value={datanasc} onChange={(e) => setData(e.target.value)}/>
                </Form.Field>
                <Form.Field>
                    <label>Bairro</label>
                    <input placeholder='Bairro' value={bairro} onChange={(e) => setBairro(e.target.value)}/>
                </Form.Field>
                <Button onClick={postData} type='submit'>Submit</Button>
            </Form>
        </div>
    )
}
